(defn android-specific? [s]
  "Returns true if the action string s can be found in the official core android sdk documentation.
These strings came either from the sdk documentation (via a calling and post processing the results of
  find . -name '*html' -exec egrep '^[[:space:]]*\\\".*\\\"[[:space:]]*$' {} \\;)
and from extracting all action string within the manifest files of the core google apps as found
in a recent android 2.2 image."
  (or 
  (contains? 
     android-core-actions
     s)))


(defn create-sdk-actions []
  (let [dir "e:/android/system/manifests"
        x (-> dir find-files mf/load-apps-from-disk)
        core-apps-actions (set (mapcat (comp (partial mapcat :actions) (partial mapcat :filters) mf/components) x))]
    (set core-apps-actions)))
  
